package com.aistudio.kaidzentracker.android.data.localDataSource.flowDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface FlowDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(flowEntity: FlowEntity)    @Query("SELECT * FROM flowEntity")    suspend fun getLocalSource(): FlowEntity?    @Query("DELETE FROM flowEntity")    suspend fun deleteLocalSource()}