package com.aistudio.kaidzentracker.android.data.localDataSource.flowDataSourceclass FlowDataSourceImpl(private val flowDao: FlowDao) : FlowDataSource {    override suspend fun insertLocalSource(flowEntity: FlowEntity) =        flowDao.insertLocalSource(flowEntity = flowEntity)    override suspend fun getLocalSource(): FlowEntity? = flowDao.getLocalSource()    override suspend fun deleteLocalSource() = flowDao.deleteLocalSource()}