package com.aistudio.kaidzentracker.android.data.localDataSource.logisticsDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface LogisticDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(logisticEntity: LogisticEntity)    @Query("SELECT * FROM logisticEntity")    suspend fun getLocalSource(): LogisticEntity?    @Query("DELETE FROM logisticEntity")    suspend fun deleteLocalSource()}