package com.aistudio.kaidzentracker.android.data.localDataSource.logisticsDataSourceclass LogisticDataSourceImpl(private val logisticDao: LogisticDao) : LogisticDataSource {    override suspend fun insertLocalSource(logisticEntity: LogisticEntity) =        logisticDao.insertLocalSource(logisticEntity = logisticEntity)    override suspend fun getLocalSource(): LogisticEntity? = logisticDao.getLocalSource()    override suspend fun deleteLocalSource() = logisticDao.deleteLocalSource()}