package com.aistudio.kaidzentracker.android.data.localDataSource.mappingDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface MappingDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(mappingEntity: MappingEntity)    @Query("SELECT * FROM mappingEntity")    suspend fun getLocalSource(): MappingEntity?    @Query("DELETE FROM mappingEntity")    suspend fun deleteLocalSource()}