package com.aistudio.kaidzentracker.android.data.localDataSource.mappingDataSourceclass MappingDataSourceImpl(private val mappingDao: MappingDao): MappingDataSource {    override suspend fun insertLocalSource(mappingEntity: MappingEntity) =        mappingDao.insertLocalSource(mappingEntity = mappingEntity)    override suspend fun getLocalSource(): MappingEntity? = mappingDao.getLocalSource()    override suspend fun deleteLocalSource() = mappingDao.deleteLocalSource()}