package com.aistudio.kaidzentracker.android.data.localDataSource.smedDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface SmedDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(smedEntity: SmedEntity)    @Query("SELECT * FROM smedEntity")    suspend fun getLocalSource(): SmedEntity?    @Query("DELETE FROM smedEntity")    suspend fun deleteLocalSource()}