package com.aistudio.kaidzentracker.android.data.localDataSource.smedDataSourceclass SmedDataSourceImpl(private val smedDao: SmedDao) : SmedDataSource {    override suspend fun insertLocalSource(smedEntity: SmedEntity) =        smedDao.insertLocalSource(smedEntity = smedEntity)    override suspend fun getLocalSource(): SmedEntity? = smedDao.getLocalSource()    override suspend fun deleteLocalSource() = smedDao.deleteLocalSource()}