package com.aistudio.kaidzentracker.android.data.localDataSource.staffInvolvementDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface StaffInvolvementDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(staffInvolvementEntity: StaffInvolvementEntity)    @Query("SELECT * FROM staffEntity")    suspend fun getLocalSource(): StaffInvolvementEntity?    @Query("DELETE FROM staffEntity")    suspend fun deleteLocalSource()}