package com.aistudio.kaidzentracker.android.data.localDataSource.staffTrainigDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface StaffTrainingDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(staffTrainingEntity: StaffTrainingEntity)    @Query("SELECT * FROM staffTrainingEntity")    suspend fun getLocalSource(): StaffTrainingEntity?    @Query("DELETE FROM staffTrainingEntity")    suspend fun deleteLocalSource()}