package com.aistudio.kaidzentracker.android.data.localDataSource.staffTrainigDataSourceclass StaffTrainingDataSourceImpl(private val staffTrainingDao: StaffTrainingDao) :    StaffTrainingDataSource {    override suspend fun insertLocalSource(staffTrainingEntity: StaffTrainingEntity) =        staffTrainingDao.insertLocalSource(staffTrainingEntity = staffTrainingEntity)    override suspend fun getLocalSource(): StaffTrainingEntity? = staffTrainingDao.getLocalSource()    override suspend fun deleteLocalSource() = staffTrainingDao.deleteLocalSource()}