package com.aistudio.kaidzentracker.android.data.localDataSource.standartWorkDataSourceimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface StandartWorkDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(standartWorkEntity: StandartWorkEntity)    @Query("SELECT * FROM standartWorkEntity")    suspend fun getLocalSource(): StandartWorkEntity?    @Query("DELETE FROM standartWorkEntity")    suspend fun deleteLocalSource()}