package com.aistudio.kaidzentracker.android.data.localDataSource.standartWorkDataSourceclass StandartWorkDataSourceImpl(    private val standartWorkDao: StandartWorkDao) : StandartWorkDataSource {    override suspend fun insertLocalSource(standartWorkEntity: StandartWorkEntity) =        standartWorkDao.insertLocalSource(standartWorkEntity = standartWorkEntity)    override suspend fun getLocalSource(): StandartWorkEntity? = standartWorkDao.getLocalSource()    override suspend fun deleteLocalSource()= standartWorkDao.deleteLocalSource()}