package com.aistudio.kaidzentracker.android.data.localDataSource.trmDataSourceimport androidx.room.*@Daointerface TrmDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertLocalSource(trmEntity: TrmEntity)    @Query("SELECT * FROM trmEntity")    suspend fun getLocalSource(): TrmEntity?    @Query("DELETE FROM trmEntity")    suspend fun deleteLocalSource()}