package com.aistudio.kaidzentracker.android.data.localDataSource.trmDataSourceclass TrmDataSourceImpl(private val trmDao: TrmDao) : TrmDataSource {    override suspend fun insertLocalSource(trmEntity: TrmEntity) =        trmDao.insertLocalSource(trmEntity = trmEntity)    override suspend fun getLocalSource(): TrmEntity? = trmDao.getLocalSource()    override suspend fun deleteLocalSource() = trmDao.deleteLocalSource()}