package com.aistudio.kaidzentracker.android.domain.staffInvolvementimport com.aistudio.kaidzentracker.android.data.localDataSource.staffInvolvementDataSource.StaffInvolvementDataSourceimport com.aistudio.kaidzentracker.android.data.localDataSource.staffInvolvementDataSource.StaffInvolvementEntityclass StaffInvolvementRepositoryImpl(private val staffInvolvementDataSource: StaffInvolvementDataSource) :    StaffInvolvementRepository {    override suspend fun get(): StaffInvolvement? =        staffInvolvementDataSource.getLocalSource()            ?.let { entity -> mapToDomain(staffInvolvementEntity = entity) }    override suspend fun delete() = staffInvolvementDataSource.deleteLocalSource()    override suspend fun save(staffInvolvement: StaffInvolvement) {        delete()        staffInvolvementDataSource.insertLocalSource(staffInvolvementEntity = mapToEntity(            staffInvolvement = staffInvolvement))    }    private fun mapToDomain(staffInvolvementEntity: StaffInvolvementEntity): StaffInvolvement =        with(staffInvolvementEntity) {            StaffInvolvement(                firstQuestions = firstQuestions ?: 0.0,                secondQuestions = secondQuestions ?: 0.0,                threeQuestions = threeQuestions ?: 0.0,                fourQuestions = fourQuestions ?: 0.0,                fiveQuestions = fiveQuestions ?: 0.0,                sixQuestions = sixQuestions ?: 0.0,                sevenQuestions = sevenQuestions ?: 0.0,                eightQuestions = eightQuestions ?: 0.0,                result = result ?: 0.0,                newResult = newResult ?: 0.0            )        }    private fun mapToEntity(staffInvolvement: StaffInvolvement?): StaffInvolvementEntity =        with(staffInvolvement) {            StaffInvolvementEntity(                firstQuestions = this?.firstQuestions,                secondQuestions = this?.secondQuestions,                threeQuestions = this?.threeQuestions,                fourQuestions = this?.fourQuestions,                fiveQuestions = this?.fiveQuestions,                sixQuestions = this?.sixQuestions,                sevenQuestions = this?.sevenQuestions,                eightQuestions = this?.eightQuestions,                result = this?.result,                newResult = this?.newResult            )        }}