package com.aistudio.kaidzentracker.android.domain.staffTrainingimport com.aistudio.kaidzentracker.android.data.localDataSource.staffTrainigDataSource.StaffTrainingDataSourceimport com.aistudio.kaidzentracker.android.data.localDataSource.staffTrainigDataSource.StaffTrainingEntityclass StaffTrainingRepositoryImpl(private val staffTrainingDataSource: StaffTrainingDataSource) :    StaffTrainingRepository {    override suspend fun get(): StaffTraining? =        staffTrainingDataSource.getLocalSource()            ?.let { entity -> mapToDomain(staffTrainingEntity = entity) }    override suspend fun delete() = staffTrainingDataSource.deleteLocalSource()    override suspend fun save(staffTraining: StaffTraining) {        delete()        staffTrainingDataSource.insertLocalSource(staffTrainingEntity = mapToEntity(staffTraining = staffTraining))    }    private fun mapToDomain(staffTrainingEntity: StaffTrainingEntity): StaffTraining =        with(staffTrainingEntity) {            StaffTraining(                firstQuestions = firstQuestions ?: 0.0,                secondQuestions = secondQuestions ?: 0.0,                threeQuestions = threeQuestions ?: 0.0,                fourQuestions = fourQuestions ?: 0.0,                fiveQuestions = fiveQuestions ?: 0.0,                sixQuestions = sixQuestions ?: 0.0,                sevenQuestions = sevenQuestions ?: 0.0,                eightQuestions = eightQuestions ?: 0.0,                result = result ?: 0.0,                newResult = newResult ?: 0.0            )        }    private fun mapToEntity(staffTraining: StaffTraining?): StaffTrainingEntity =        with(staffTraining) {            StaffTrainingEntity(                firstQuestions = this?.firstQuestions,                secondQuestions = this?.secondQuestions,                threeQuestions = this?.threeQuestions,                fourQuestions = this?.fourQuestions,                fiveQuestions = this?.fiveQuestions,                sixQuestions = this?.sixQuestions,                sevenQuestions = this?.sevenQuestions,                eightQuestions = this?.eightQuestions,                result = this?.result,                newResult = this?.newResult            )        }}