package com.aistudio.kaidzentracker.android.domain.trmimport com.aistudio.kaidzentracker.android.data.localDataSource.trmDataSource.TrmDataSourceimport com.aistudio.kaidzentracker.android.data.localDataSource.trmDataSource.TrmEntityclass TrmRepositoryImpl(private val trmDataSource: TrmDataSource) : TrmRepository {    override suspend fun get(): Trm? =        trmDataSource.getLocalSource()?.let { entity -> mapToDomain(entity) }    override suspend fun delete() = trmDataSource.deleteLocalSource()    override suspend fun save(trm: Trm) {        delete()        trmDataSource.insertLocalSource(trmEntity = mapToEntity(trm = trm))    }    private fun mapToDomain(trmEntity: TrmEntity): Trm = with(trmEntity) {        Trm(            firstQuestions = firstQuestions ?: 0.0,            secondQuestions = secondQuestions ?: 0.0,            threeQuestions = threeQuestions ?: 0.0,            fourQuestions = fourQuestions ?: 0.0,            fiveQuestions = fiveQuestions ?: 0.0,            result = result ?: 0.0,            newResult = newResult ?: 0.0        )    }    private fun mapToEntity(trm: Trm): TrmEntity = with(trm) {        TrmEntity(            firstQuestions = firstQuestions,            secondQuestions = secondQuestions,            threeQuestions = threeQuestions,            fourQuestions = fourQuestions,            fiveQuestions = fiveQuestions,            result = result,            newResult = newResult        )    }}