package com.aistudio.kaidzentracker.android.screensimport android.widget.Toastimport androidx.compose.foundation.ExperimentalFoundationApiimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.runtime.*import androidx.compose.runtime.livedata.observeAsStateimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.aistudio.kaidzentracker.android.screens.commonScreenComponents.*import com.aistudio.kaidzentracker.android.theme.PrimaryVariantimport com.aistudio.kaidzentracker.android.ui.viewmodels.MappingScreenViewModelimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.ApplicationIntentimport org.koin.androidx.compose.koinViewModelimport kotlin.math.floor@OptIn(ExperimentalFoundationApi::class)@Composablefun MappingScreen(    modifier: Modifier,    navController: NavController,    mappingScreenViewModel: MappingScreenViewModel = koinViewModel(),) {    val context = LocalContext.current    val state = mappingScreenViewModel.mappingScreenState.observeAsState().value    var visible by remember { mutableStateOf(false) }    when (state!!.isLoading) {        false -> Box(modifier = modifier            .fillMaxSize()            .background(color = Color.White)) {            LazyColumn(modifier = modifier.padding(bottom = 45.dp)) {                item {                    CommonLogoAppBar(modifier = modifier, isAnimated = visible)                }                stickyHeader {                    CommonHeaderText(                        modifier = modifier,                        textHeader = "КАРТИРОВАНИЕ",                        colorBackgraund = PrimaryVariant,                        height = 28,                        shape = 5,                        paddingHorizontal = 6                    )                }                item {                    CommonTableHeader(modifier = modifier)                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.firstQuestions.toInt(),                        onValueChange = { firstQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedFirstQuestions(                                newResult = firstQuestions))                        },                        numberQuestions = "1",                        textQuestions = "Процент процессов, для которых составлены КПСЦ(VCM)",                        textRecommendation = "1. Составить перечень технологических и бизнеспроцессов подразделения\n" +                                "2. Организовать составление VSM процессов\n" +                                "3. Составить интеллект-карту учета картируемых процессов")                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.secondQuestions.toInt(),                        onValueChange = { secondQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedSecondQuestions(                                newResult = secondQuestions))                        },                        numberQuestions = "2",                        textQuestions = "Для выявления коренных причин появления проблем используются методы решения проблем (есть документальное подтверждение). (Нет – 0 / Всегда – 10)",                        textRecommendation = "Для анализа проблем используется \"Таблица поиска потерь и выработки решений\"")                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.threeQuestions.toInt(),                        onValueChange = { threeQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedThreeQuestions(                                newResult = threeQuestions))                        },                        numberQuestions = "3",                        textQuestions = "Процент процессов, для которых разработаны карты потоков будущего состояния.",                        textRecommendation = "1. На основе принятых решений разработать VSM будущего состояния приоритетных процессов\n" +                                "2. Организовать учет разрабатываемых VSM будущего состояния приоритетных процессов")                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.fourQuestions.toInt(),                        onValueChange = { fourQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedFourQuestions(                                newResult = fourQuestions))                        },                        numberQuestions = "4",                        textQuestions = "Процент процессов, в которых разработаны мероприятия по оптимизации потока на основе картирования",                        textRecommendation = "Разработать план мероприятий по улучшению потока в процессах")                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.fiveQuestions.toInt(),                        onValueChange = { fiveQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedFiveQuestions(                                newResult = fiveQuestions))                        },                        numberQuestions = "5",                        textQuestions = "Процент процессов, для которых разработаны диаграммы спагетти текущего и будущего состояния",                        textRecommendation = "1. Разработать карты спагетти приоритетных процессов\n" +                                "2. Организовать учет разрабатываемых диаграмм спагетти текущего и будущего состояния приоритетных процессов")                }                item {                    CommonButtonsInput(modifier = modifier,                        value = state.sixQuestions.toInt(),                        onValueChange = { sixQuestions ->                            mappingScreenViewModel.getIntent(intent = ApplicationIntent.ChangedSixQuestions(                                newResult = sixQuestions))                        },                        numberQuestions = "6",                        textQuestions = "Процент процессов, для которых разработаны и оформлены диаграммы загрузки персонала/процессов для текущего и будущего состояния потока создания ценности",                        textRecommendation = "Разработать диаграммы и схемы загрузки персонала до и после преобразований")                }                item {                    val result = floor(state.result * 10) / 10.0                    ResultView(modifier = modifier,                        textResult = result.toString(),                        text = "Итог")                }                item {                    val newResult = floor(state.newResult * 10) / 10.0                    ResultView(modifier = modifier,                        textResult = newResult.toString(),                        text = "Предыдущее значение")                }            }            CommonButtons(                modifier = modifier.align(Alignment.BottomCenter),                onClickSave = {                    Toast.makeText(context, "Сохранено", Toast.LENGTH_SHORT).show()                    mappingScreenViewModel.getIntent(intent = ApplicationIntent.SaveClicked)                },                onClickDelete = {                    Toast.makeText(context, "Удалено", Toast.LENGTH_SHORT).show()                    mappingScreenViewModel.getIntent(intent = ApplicationIntent.DeletedClicked)                },                onClickNext = { navController.navigate(route = "flowScreen") },                onClickBack = { navController.popBackStack() }                ,navController = navController            )        }        true -> FullScreenLoaderComponent(modifier = modifier)    }    LaunchedEffect(key1 = Unit, block = {        mappingScreenViewModel.getIntent(intent = ApplicationIntent.InitialScreen)        visible = true    })}