package com.aistudio.kaidzentracker.android.screensimport android.view.ViewGroupimport android.webkit.WebViewimport android.webkit.WebViewClientimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.compose.ui.viewinterop.AndroidViewimport androidx.navigation.NavControllerimport com.aistudio.kaidzentracker.android.screens.commonScreenComponents.ButtonStartimport com.aistudio.kaidzentracker.android.screens.commonScreenComponents.CommonButtons@Composablefun ResumeScreen(modifier: Modifier, navController: NavController) {    val url = "https://leannext.pro/sergei_korchagin"    Box(modifier = modifier.fillMaxSize()) {        AndroidView(factory = {            WebView(it).apply {                layoutParams = ViewGroup.LayoutParams(                    ViewGroup.LayoutParams.MATCH_PARENT,                    ViewGroup.LayoutParams.MATCH_PARENT                )                webViewClient = WebViewClient()                loadUrl(url)            }        }, update = {            it.loadUrl(url)        })        ButtonStart(modifier = modifier.padding(bottom = 20.dp).align(Alignment.BottomCenter), onClick = { navController.navigate(route = "kdiScreen") }, textButtons = "Вернуться к вопросам" )    }}