package com.aistudio.kaidzentracker.android.ui.viewmodelsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.aistudio.kaidzentracker.android.domain.flow.Flowimport com.aistudio.kaidzentracker.android.domain.flow.FlowRepositoryimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.ApplicationIntentimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.IntentHandlerimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchclass FlowScreenViewModel(private val flowRepository: FlowRepository) : ViewModel(),    IntentHandler<ApplicationIntent> {    private val _flowScreenState: MutableLiveData<Flow> =        MutableLiveData(Flow())    val flowScreenState: LiveData<Flow> = _flowScreenState    override fun getIntent(intent: ApplicationIntent) {        when (val curentViewState = _flowScreenState.value) {            is Flow -> intent(intent, curentViewState)        }    }    private fun intent(intent: ApplicationIntent, curentViewState: Flow) {        when (intent) {            is ApplicationIntent.InitialScreen -> loadDataScreen()            is ApplicationIntent.ChangedFirstQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(firstQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSecondQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(secondQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedThreeQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(threeQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFourQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(fourQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFiveQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(fiveQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSixQuestions -> _flowScreenState.postValue(                _flowScreenState.value?.copy(sixQuestions = intent.newResult.toDouble()))            is ApplicationIntent.SaveClicked -> saveData(state = curentViewState)            is ApplicationIntent.DeletedClicked -> deleteAll()            else -> {}        }    }    private fun saveData(state: Flow) {        viewModelScope.launch(Dispatchers.IO) {            val calc = (state.firstQuestions+                    state.secondQuestions +                    state.threeQuestions +                    state.fourQuestions +                    state.fiveQuestions +                    state.sixQuestions                    ) / 6.toDouble()            flowRepository.save(flow = Flow(                firstQuestions = state.firstQuestions,                secondQuestions = state.secondQuestions,                threeQuestions = state.threeQuestions,                fourQuestions = state.fourQuestions,                fiveQuestions = state.fiveQuestions,                sixQuestions = state.sixQuestions,                result = calc ,                newResult = if(state.result == calc) calc else state.result))            loadDataScreen()        }    }    private fun loadDataScreen() {        viewModelScope.launch(Dispatchers.IO) {            _flowScreenState.postValue(Flow(isLoading = true))            with(flowRepository.get()) {                _flowScreenState.postValue(_flowScreenState.value?.copy(                    isLoading = false,                    firstQuestions = this?.firstQuestions ?: 0.0,                    secondQuestions = this?.secondQuestions ?: 0.0,                    threeQuestions = this?.threeQuestions ?: 0.0,                    fourQuestions = this?.fourQuestions ?: 0.0,                    fiveQuestions = this?.fiveQuestions ?: 0.0,                    sixQuestions = this?.sixQuestions ?: 0.0,                    result = this?.result ?: 0.0,                    newResult = this?.newResult ?: 0.0))            }        }    }    private fun deleteAll() {        viewModelScope.launch(Dispatchers.IO) {            flowRepository.delete()            loadDataScreen()        }    }}