package com.aistudio.kaidzentracker.android.ui.viewmodelsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.aistudio.kaidzentracker.android.domain.logistic.Logisticimport com.aistudio.kaidzentracker.android.domain.logistic.LogisticRepositoryimport com.aistudio.kaidzentracker.android.domain.staffTraining.StaffTrainingimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.ApplicationIntentimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.IntentHandlerimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchclass LogisticScreenViewModel(private val logisticRepository: LogisticRepository) : ViewModel(),    IntentHandler<ApplicationIntent> {    private val _logisticScreenState: MutableLiveData<Logistic> =        MutableLiveData(Logistic())    val logisticScreenState: LiveData<Logistic> = _logisticScreenState    override fun getIntent(intent: ApplicationIntent) {        when (val currentViewState = _logisticScreenState.value) {            is Logistic -> intent(intent, currentViewState)        }    }    private fun intent(intent: ApplicationIntent, currentViewState: Logistic) {        when (intent) {            is ApplicationIntent.InitialScreen -> loadDataScreen()            is ApplicationIntent.ChangedFirstQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(firstQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSecondQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(secondQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedThreeQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(threeQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFourQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(fourQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFiveQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(fiveQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSixQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(sixQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSevenQuestions -> _logisticScreenState.postValue(                _logisticScreenState.value?.copy(sevenQuestions = intent.newResult.toDouble()))            is ApplicationIntent.SaveClicked -> saveData(state = currentViewState)            is ApplicationIntent.DeletedClicked -> deleteAll()            else -> {}        }    }    private fun deleteAll() {        viewModelScope.launch(Dispatchers.IO) {            logisticRepository.delete()            loadDataScreen()        }    }    private fun saveData(state: Logistic) {        viewModelScope.launch(Dispatchers.IO) {            val calc = (state.firstQuestions +                    state.secondQuestions +                    state.threeQuestions +                    state.fourQuestions +                    state.fiveQuestions +                    state.sixQuestions +                    state.sevenQuestions ) / 7.toDouble()            logisticRepository.save(logistic = Logistic(                firstQuestions = state.firstQuestions,                secondQuestions = state.secondQuestions,                threeQuestions = state.threeQuestions,                fourQuestions = state.fourQuestions,                fiveQuestions = state.fiveQuestions,                sixQuestions = state.sixQuestions,                sevenQuestions = state.sevenQuestions,                result = calc,                newResult = if (state.result == calc) calc else state.result))            loadDataScreen()        }    }    private fun loadDataScreen() {        viewModelScope.launch(Dispatchers.IO) {            _logisticScreenState.postValue(Logistic(isLoading = true))            with(logisticRepository.get()) {                _logisticScreenState.postValue(_logisticScreenState.value?.copy(                    isLoading = false,                    firstQuestions = this?.firstQuestions ?: 0.0,                    secondQuestions = this?.secondQuestions ?: 0.0,                    threeQuestions = this?.threeQuestions ?: 0.0,                    fourQuestions = this?.fourQuestions ?: 0.0,                    fiveQuestions = this?.fiveQuestions ?: 0.0,                    sixQuestions = this?.sixQuestions ?: 0.0,                    sevenQuestions = this?.sevenQuestions ?: 0.0,                    result = this?.result ?: 0.0,                    newResult = this?.newResult ?: 0.0)                )            }        }    }}