package com.aistudio.kaidzentracker.android.ui.viewmodelsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.aistudio.kaidzentracker.android.domain.smed.Smedimport com.aistudio.kaidzentracker.android.domain.smed.SmedRepositoryimport com.aistudio.kaidzentracker.android.domain.trm.Trmimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.ApplicationIntentimport com.aistudio.kaidzentracker.android.ui.viewmodels.models.IntentHandlerimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchclass SmedViewModel(private val smedRepository: SmedRepository) : ViewModel(),    IntentHandler<ApplicationIntent> {    private val _smedScreenState: MutableLiveData<Smed> =        MutableLiveData(Smed())    val smedScreenState: LiveData<Smed> = _smedScreenState    override fun getIntent(intent: ApplicationIntent) {        when (val curentViewState = _smedScreenState.value) {            is Smed -> intent(intent, curentViewState)        }    }    private fun intent(intent: ApplicationIntent, curentViewState: Smed) {        when (intent) {            is ApplicationIntent.InitialScreen -> loadDataScreen()            is ApplicationIntent.ChangedFirstQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(firstQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSecondQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(secondQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedThreeQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(threeQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFourQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(fourQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedFiveQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(fiveQuestions = intent.newResult.toDouble()))            is ApplicationIntent.ChangedSixQuestions -> _smedScreenState.postValue(                _smedScreenState.value?.copy(sixQuestions = intent.newResult.toDouble()))            is ApplicationIntent.SaveClicked -> saveData(state = curentViewState)            is ApplicationIntent.DeletedClicked -> deleteAll()            else -> {}        }    }    private fun saveData(state: Smed) {        viewModelScope.launch(Dispatchers.IO) {            val calc = (state.firstQuestions+                    state.secondQuestions +                    state.threeQuestions +                    state.fourQuestions +                    state.fiveQuestions +                    state.sixQuestions                    ) / 6.toDouble()            smedRepository.save(smed = Smed(                firstQuestions = state.firstQuestions,                secondQuestions = state.secondQuestions,                threeQuestions = state.threeQuestions,                fourQuestions = state.fourQuestions,                fiveQuestions = state.fiveQuestions,                sixQuestions = state.sixQuestions,                result = calc ,                newResult = if(state.result == calc) calc else state.result))            loadDataScreen()        }    }    private fun loadDataScreen() {        viewModelScope.launch(Dispatchers.IO) {            _smedScreenState.postValue(Smed(isLoading = true))            with(smedRepository.get()) {                _smedScreenState.postValue(_smedScreenState.value?.copy(                    isLoading = false,                    firstQuestions = this?.firstQuestions ?: 0.0,                    secondQuestions = this?.secondQuestions ?: 0.0,                    threeQuestions = this?.threeQuestions ?: 0.0,                    fourQuestions = this?.fourQuestions ?: 0.0,                    fiveQuestions = this?.fiveQuestions ?: 0.0,                    sixQuestions = this?.sixQuestions ?: 0.0,                    result = this?.result ?: 0.0,                    newResult = this?.newResult ?: 0.0))            }        }    }    private fun deleteAll() {        viewModelScope.launch(Dispatchers.IO) {            smedRepository.delete()            loadDataScreen()        }    }}